import React, { Component } from 'react';

import ConstantList from '../../appConfig';
import { Avatar, Grid, Button, withStyles } from '@material-ui/core';

import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import localStorageService from '../../services/localStorageService';

import {
	getCurrentUser,
	saveItem,
	saveUser,
	getUserByUsername,
	saveOrUpdateUser,
	getListHealthOrgByUser,
} from './UserProfileService';
import UploadCropImagePopup from './UploadCropImagePopup';
import ChangePasswordDiaglog from './ChangePasswordPopup';
import authService from '../../services/jwtAuthService';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../../../styles/views/_style.scss';
import history from 'history.js';
const API_PATH = ConstantList.API_ENPOINT + '/api/fileUpload/';

toast.configure({
	autoClose: 1000,
	draggable: false,
	limit: 3,
});
class UserProfile extends Component {
	state = {
		displayName: '',
		firstName: '',
		lastName: '',
		userID: '',
		open: true,
		user: {},
		healthOrg: {},
		shouldOpenImageDialog: false,
		shouldOpenPasswordDialog: false,
		checkHealthOrg: false,
		name: '',
		taxCodeOfTheUnit: '',
		code: '',
		specifyTestPurpose: '',
		address: '',
		specifyLevel: '',
		positiveAffirmativeRight: true,
		shouldOpenSearchDialog: false,
		shouldOpenConfirmationDialog: false,
		qualificationSelect: [],
		qualification: {},
		officerPosion: '',
		unitCodeOfProgramPEQAS: '',
		testpurposeSelect: [],
		testPurpose1: {},
		testPurpose2: {},
		testPurpose3: {},
		testPurpose4: {},
		levelHealOrg: [],
		healthOrgTypeSelect: [],
		healthOrgType: [],
		administrativeUnit: '',
		fax: '',
		isView: false,
		shouldOpenAdministrativeUnitsPopup: false,
		level: {},
		sampleReceiptDate: new Date(),
		sampleRecipient: '',
		specifySampleStatus: '',
		specifyQualification: '',
		isManualSetCode: false,
		hasErrorLever: false,
		levelId: '',
		loading: false,
	};

	windowResizeListener;

	toggleSidenav = () => {
		this.setState({
			open: !this.state.open,
		});
	};

	handleWindowResize = () => {
		return (event) => {
			if (event.target.innerWidth < 768) {
				this.setState({ mobile: true });
			} else this.setState({ mobile: false });
		};
	};

	componentDidMount() {
		getCurrentUser().then(({ data }) => {
			this.setState({ user: data });
			// console.log(this.state.user)

			this.setState({ ...data });
			let { user } = this.state;
			let displayName = user.firstName + ' ' + user.lastName;
			this.setState({
				displayName: displayName,
				firstName: user.firstName,
				lastName: user.lastName,
				userID: user.id,
			});
		});
		//let user = localStorageService.getLoginUser();
		if (window.innerWidth < 768) {
			this.setState({ open: false });
		}
		if (window)
			this.windowResizeListener = window.addEventListener('resize', (event) => {
				if (event.target.innerWidth < 768) {
					this.setState({ open: false });
				} else this.setState({ open: true });
