		let { firstName, lastName, displayName } = this.state;
		if (source === 'firstName') {
			displayName = event.target.value + ' ' + lastName;
		} else if (source === 'lastName') {
			displayName = firstName + ' ' + event.target.value;
		}

		this.setState({ displayName: displayName });
